import React, { useState, useEffect } from "react";
import axios from "axios";

const Holdings = () => {
  const [allHoldings, setAllHoldings] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3002/allpositions")  // or /allholdings depending on your need
      .then((res) => {
        if (Array.isArray(res.data)) {
          setAllHoldings(res.data);
        } else {
          console.error("Unexpected response format:", res.data);
          setAllHoldings([]);
        }
      })
      .catch((err) => {
        console.error("Failed to fetch holdings:", err);
        setAllHoldings([]);
      });
  }, []);

  if (!allHoldings.length) {
    return <div>No holdings data available.</div>;  // helpful to debug empty data
  }

  return (
   <>
  <h3 className="mt-3">Holdings ({allHoldings.length})</h3>
  <div className="table-responsive">
    <table className="table table-bordered table-striped table-hover align-middle">
      <thead className="table-light">
        <tr>
          <th>Instrument</th>
          <th>Qty.</th>
          <th>Avg. cost</th>
          <th>LTP</th>
          <th>Cur. val</th>
          <th>P&L</th>
          <th>Net chg.</th>
          <th>Day chg.</th>
        </tr>
      </thead>
      <tbody>
        {allHoldings.map((stock, index) => {
          const curValue = stock.price * stock.qty;
          const profitLoss = curValue - stock.avg * stock.qty;
          const isProfit = profitLoss >= 0;
          const profClass = isProfit ? "text-success fw-bold" : "text-danger fw-bold";
          const dayClass = stock.isLoss ? "text-danger fw-bold" : "text-success fw-bold";

          return (
            <tr key={index}>
              <td>{stock.name}</td>
              <td>{stock.qty}</td>
              <td>{stock.avg.toFixed(2)}</td>
              <td>{stock.price.toFixed(2)}</td>
              <td>{curValue.toFixed(2)}</td>
              <td className={profClass}>{profitLoss.toFixed(2)}</td>
              <td className={profClass}>{stock.net}</td>
              <td className={dayClass}>{stock.day}</td>
            </tr>
          );
        })}
      </tbody>
    </table>
  </div>
</>
  );
};

export default Holdings;
